import gcimport sysimport timeimport pandas as pdimport streamlit as stfrom streamlit_server_state import no_rerun, server_statefrom helper.modelling import determine_rerun_reinitialize, initialize, set_static_model_paramsfrom helper.own_corpus import check_table_exists, process_corpus, transfer_dbfrom helper.progress_bar import Loggerfrom helper.ui import export_chat_history, import_styles, streamed_response, ui_advanced_model_params, ui_end_session, ui_lockout_reset, ui_model_params, ui_upload_docsfrom helper.user_management import calc_max_users, check_password, clear_models, determine_availability, manage_boot, setup_local_files, update_server_state### session initialization/loginif "user_name" in st.session_state:    from helper.user_management import record_usedetermine_availability()if not check_password():    st.stop()  # Do not continue if check_password is not True.        ### initial setup# headerst.title("Local LLM")if "master_db_name" not in st.session_state:    st.session_state["master_db_name"] = "vector_db"if "db_name" not in st.session_state:    st.session_state["db_name"] = st.session_state["user_name"].lower().replace(" ", "_")    # check if first boot of user and check if eligible to be kicked offmanage_boot()        # styles sheetsimport_styles()# parameters/authenticationsetup_local_files()# placeholder on initial loadif f'model_{st.session_state["db_name"]}' not in server_state:    st.markdown("""<div class="icon_text"><img width=50 src='https://www.svgrepo.com/show/375527/ai-platform.svg'></div>""", unsafe_allow_html=True)    st.markdown("""<div class="icon_text"<h4>What would you like to know?</h4></div>""", unsafe_allow_html=True)### sidebar# upload your own documents sectionui_upload_docs()st.sidebar.divider()# model parametersui_model_params()# advanced model parametersui_advanced_model_params()st.sidebar.divider()# lockout and reset memory buttonui_lockout_reset()### model    # static model paramsset_static_model_params()# determine if the database needs to be reinitializeddetermine_rerun_reinitialize()# loading modelif f'model_{st.session_state["db_name"]}' not in server_state or st.session_state["reinitialize"] or st.session_state["reinitialize_remake"] or st.session_state["process_corpus_button"]:    if st.session_state["process_corpus_button"]:        if not((st.session_state["new_corpus_name"] == f"temporary_{st.session_state['db_name']}") or (st.session_state["new_corpus_name"] not in list(st.session_state["corpora_dict"].name.values))):            st.error("A corpus with this name already exists, choose another one.")        else:            with st.spinner('Processing corpus...'):                record_use(future_lock=True)                old_stdout = sys.stdout                sys.stdout = Logger(st.progress(0), st.empty())                st.session_state["corpora_dict"] = process_corpus(user_name=st.session_state["db_name"], corpus_name=st.session_state["new_corpus_name"], own_urls=st.session_state["own_urls"], uploaded_document=st.session_state["uploaded_file"])                record_use(future_lock=False)                            st.session_state["selected_corpus"] = st.session_state["new_corpus_name"]            st.session_state.messages = [] # clear out message history on the prior context            clear_models()        with st.spinner('Initializing...'):        # whether or not to remake the vector DB        if st.session_state["reinitialize_remake"] or st.session_state["process_corpus_button"]:            rerun_populate_db = True            clear_database_local = st.session_state["clear_database"]        else:            rerun_populate_db = st.session_state["rerun_populate_db"]            clear_database_local = st.session_state["clear_database"]                    def model_initialization():            model, st.session_state["which_llm"], st.session_state["which_corpus"] = initialize(                which_llm_local=st.session_state["selected_llm"],                which_corpus_local=None if st.session_state["selected_corpus"] == "None" else st.session_state["selected_corpus"],                n_gpu_layers=st.session_state["n_gpu_layers"],                temperature=st.session_state["temperature"] / 1e2, # convert 1-100 to 0-1                max_new_tokens=st.session_state["max_new_tokens"],                context_window=st.session_state["context_window"],                memory_limit=st.session_state["memory_limit"],                chunk_overlap=st.session_state["chunk_overlap"],                chunk_size=st.session_state["chunk_size"],                paragraph_separator=st.session_state["paragraph_separator"],                separator=st.session_state["separator"],                system_prompt=st.session_state["system_prompt"],                rerun_populate_db=rerun_populate_db,                clear_database_local=clear_database_local,                corpora_dict=st.session_state["corpora_dict"],                llm_dict=st.session_state["llm_dict"],                db_name=st.session_state["db_name"],                db_info=st.session_state["db_info"],            )            update_server_state(f'model_{st.session_state["db_name"]}', model)            del model            gc.collect()                record_use(future_lock=True)        model_initialization()        record_use(future_lock=False)                # clear the progress bar        if rerun_populate_db:            try:                sys.stdout = sys.stdout.clear()                sys.stdout = old_stdout            except:                pass                    # copy the new table to master vector_db if it's not already there        if not(check_table_exists(user=st.session_state["db_info"].loc[0, 'user'], password=st.session_state["db_info"].loc[0, 'password'], db_name=st.session_state["master_db_name"], table_name=f"data_{st.session_state['which_corpus']}")):            # close the model connection to not have simulataneous ones            clear_models()                        # transfer the db            transfer_db(user=st.session_state["db_info"].loc[0, 'user'], password=st.session_state["db_info"].loc[0, 'password'], source_db=st.session_state["db_name"], target_db=st.session_state["master_db_name"])                        # reinitialize the model            record_use(future_lock=True)            model_initialization()            record_use(future_lock=False)                    st.session_state.messages = [] # clear out message history on the prior context        st.info("Model successfully initialized!")                update_server_state("max_users", calc_max_users(len(server_state["queue"])))if f'model_{st.session_state["db_name"]}' in server_state:    # Initialize chat history    if "messages" not in st.session_state:        st.session_state.messages = []        # Display chat messages from history on app rerun    for message in st.session_state.messages:        avatar = st.session_state["user_avatar"] if message["role"] == "user" else st.session_state["assistant_avatar"]        with st.chat_message(message["role"], avatar=avatar):            if "source_string" not in message["content"]:                st.markdown(message["content"])            else:                st.markdown("Sources: ", unsafe_allow_html=True, help=message["content"].split("string:")[1])                    # reset model's memory    if st.session_state["reset_memory"]:        if server_state[f'model_{st.session_state["db_name"]}'].chat_engine is not None:            with no_rerun:                server_state[f'model_{st.session_state["db_name"]}'].chat_engine.reset()        with st.chat_message("assistant", avatar=st.session_state["assistant_avatar"]):            st.markdown("Model memory reset!")        st.session_state.messages.append({"role": "assistant", "content": "Model memory reset!"})        # Accept user input    if st.session_state["which_corpus"] is None:        placeholder_text = f"""Query '{st.session_state["which_llm"]}', not contextualized"""    else:        placeholder_text = f"""Query '{st.session_state["which_llm"]}' contextualized on '{st.session_state["which_corpus"]}' corpus"""            if prompt := st.chat_input(placeholder_text):        # Display user message in chat message container        with st.chat_message("user", avatar=st.session_state["user_avatar"]):            st.markdown(prompt)        # Add user message to chat history        st.session_state.messages.append({"role": "user", "content": prompt})                if st.session_state.messages[-1]["content"].lower() == "clear":            clear_models()                with st.chat_message("assistant", avatar=st.session_state["assistant_avatar"]):                st.markdown("Models cleared!")            st.session_state.messages.append({"role": "assistant", "content": "Models cleared!"})        else:            # lock the model to perform requests sequentially            if "in_use" not in server_state:                update_server_state("in_use", False)                            if "exec_queue" not in server_state:                update_server_state("exec_queue", [st.session_state["user_name"]])            if len(server_state["exec_queue"]) == 0:                update_server_state("exec_queue", [st.session_state["user_name"]])            else:                if st.session_state["user_name"] not in server_state["exec_queue"]:                    # add to the queue                    update_server_state("exec_queue", server_state["exec_queue"] + [st.session_state["user_name"]])                            with st.spinner('Query queued...'):                t = st.empty()                while server_state["in_use"] or server_state["exec_queue"][0] != st.session_state["user_name"]:                    t.markdown(f'You are place {server_state["exec_queue"].index(st.session_state["user_name"])} of {len(server_state["exec_queue"]) - 1}')                    time.sleep(1)                t.empty()                                # lock the model while generating            update_server_state("in_use", True)            record_use(future_lock=True)                            # generate response                            response = server_state[f'model_{st.session_state["db_name"]}'].gen_response(                st.session_state.messages[-1]["content"],                similarity_top_k=st.session_state["similarity_top_k"],                use_chat_engine=st.session_state["use_chat_engine"],                reset_chat_engine=st.session_state["reset_chat_engine"],                streaming=True,            )            # Display assistant response in chat message container            with st.chat_message("assistant", avatar=st.session_state["assistant_avatar"]):                st.write_stream(streamed_response(response["response"]))                    # adding sources            with st.chat_message("assistant", avatar=st.session_state["assistant_avatar"]):                if len(response.keys()) > 1: # only do if RAG                    # markdown help way                    source_string = ""                    counter = 1                    for j in list(pd.Series(list(response.keys()))[pd.Series(list(response.keys())) != "response"]):                        #source_string += f"**Source {counter}**:\n\n \t\t{response[j]}\n\n\n\n"                        metadata_dict = eval(response[j].split("| source text:")[0].replace("metadata: ", ""))                        metadata_string = ""                        for key, value in metadata_dict.items():                            if key != "is_csv":                                metadata_string += f"'{key}': '{value}'\n"                                                source_string += f"""# Source {counter}\n ### Metadata:\n ```{metadata_string}```\n ### Text:\n{response[j].split("| source text:")[1]}\n\n"""                        counter += 1                else:                    source_string = "NA"                st.markdown("Sources: ", unsafe_allow_html=True, help = f"{source_string}")                            # unlock the model            update_server_state("in_use", False)            update_server_state("exec_queue", server_state["exec_queue"][1:]) # take out of the queue                        record_use(future_lock=False)                    # Add assistant response to chat history            st.session_state.messages.append({"role": "assistant", "content": response["response"].response})            st.session_state.messages.append({"role": "assistant", "content": f"source_string:{source_string}"})            # export chat historyif "messages" in st.session_state:    export_chat_button = st.sidebar.download_button(        label="Export chat history",        data=export_chat_history(),        file_name="chat_history.MD",        help="Export the session's chat history to a formatted Markdown file. If you don't have a Markdown reader on your computer, post the contents to a [web app](http://editor.md.ipandao.com/en.html)"    )        ### final UI elementsui_end_session()